  useEffect(() => {
    setMenuOpen(false)
  }, [location.pathname])

  return (
    <AnimatePresence>
      {isOpen && (
        <MenuWrapper
          variants={menuVariants}
          initial='closed'
          exit='closed'
          animate={isOpen ? 'open' : 'closed'}
